import React, {useState} from 'react'
import '../App.css'


const Form = () => {
    const [input, setInput] = useState({
        name: "",
        image: "",
        age: "",
        note: ""
    })

    const handleChange = (event) => {
        const [name, target] = event
        setInput({[name]: target})
    }
    return (
        <div className={"add-to-list"}>
            <input type="text" className={"add-to-list-input"} placeholder={"name"} value={input.name}/>
            <input type="text" className={"add-to-list-input"} placeholder={"image"} value={input.image}/>
            <input type="text" className={"add-to-list-input"} placeholder={"age"} value={input.age}/>
            <textarea className={"add-to-list-input"} placeholder={"note"} value={input.note}/>
        </div>
    )
}

export default Form
